import React, { useEffect, useState } from 'react';
import Board from './components/Board';
import { connectWebSocket, sendMoveCommand } from './websocket';

function App() {
    const [grid, setGrid] = useState([]);
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        const { disconnect } = connectWebSocket((message) => {
            console.log('Received message:', message);

            try {
                const gridData = JSON.parse(message.body);
                console.log('Parsed grid data:', gridData);

                if (Array.isArray(gridData) && Array.isArray(gridData[0])) {
                    setGrid(gridData);
                } else {
                    console.error('Invalid grid data format:', gridData);
                }
            } catch (error) {
                console.error('Error parsing message body:', error);
            }

            setMessages((prevMessages) => [...prevMessages, message.body]);
        });

        return () => {
            disconnect(); 
        };
    }, []);

    const handleMove = (move) => {
        sendMoveCommand(move);
    };

    return (
        <div>
            <h1>WebSocket App</h1>
            <button onClick={() => handleMove({ x: 1, y: 2 })}>Send Move</button>
            <div>
                <h2>Messages:</h2>
                <ul>
                    {messages.map((msg, index) => (
                        <li key={index}>{msg}</li>
                    ))}
                </ul>
            </div>
            <Board grid={grid} />
        </div>
    );
}

export default App;
