import React, { useEffect, useState } from 'react';
import Board from './components/Board';
import DeploymentForm from './components/DeploymentForm';
import { connectWebSocket, sendDeploymentData, sendMoveCommand } from './websocket';

function App() {
    const [grid, setGrid] = useState(Array(5).fill(null).map(() => Array(5).fill(null)));
    const [messages, setMessages] = useState([]);
    const [deploymentData, setDeploymentData] = useState([]);

    useEffect(() => {
        const { disconnect } = connectWebSocket((message) => {
            try {
                const data = JSON.parse(message.body);
                console.log('Received data:', data);

                if (Array.isArray(data) && Array.isArray(data[0])) {
                    setGrid(data);
                } else if (Array.isArray(data)) {
                    // Remove quotes if necessary and update state
                    const sanitizedData = data.map(item => item.replace(/"/g, ''));
                    setDeploymentData(sanitizedData);
                } else {
                    setMessages((prevMessages) => [...prevMessages, message.body]);
                }
            } catch (error) {
                console.error('Error parsing message:', error);
                setMessages((prevMessages) => [...prevMessages, 'Invalid message format']);
            }
        });

        return () => {
            disconnect();
        };
    }, []);

    const handleMove = (move) => {
        sendMoveCommand(move);
    };

    const handleDeployment = (deployment) => {
        sendDeploymentData(deployment);
    };

    return (
        <div>
            <h1>WebSocket App</h1>
            <button onClick={() => handleMove({ x: 1, y: 2 })}>Send Move</button>
            <DeploymentForm onDeploy={handleDeployment} />
            <div>
                <h2>Messages:</h2>
                <ul>
                    {messages.map((msg, index) => (
                        <li key={index}>{msg}</li>
                    ))}
                </ul>
            </div>
            <h2>Board:</h2>
            <Board grid={grid} />
        </div>
    );
}

export default App;
