import React, { useEffect, useState } from 'react';
import Board from './components/Board';
import DeploymentForm from './components/DeploymentForm';
import { connectWebSocket, sendMoveCommand } from './websocket';

function App() {
    const [grid, setGrid] = useState(Array(5).fill(Array(5).fill(null)));
    const [messages, setMessages] = useState([]);
    const [deployment, setDeployment] = useState(null); // Manage deployment data
    const [isDeploymentComplete, setIsDeploymentComplete] = useState(false); // Track deployment status

    useEffect(() => {
        const { disconnect } = connectWebSocket((message) => {
            try {
                const data = JSON.parse(message.body);
                console.log('Received data:', data);

                if (Array.isArray(data) && Array.isArray(data[0])) {
                    setGrid(data);
                } else {
                    setMessages((prevMessages) => [...prevMessages, message.body]);
                }
            } catch (error) {
                console.error('Error parsing message:', error);
                setMessages((prevMessages) => [...prevMessages, 'Invalid message format']);
            }
        });

        return () => {
            disconnect();
        };
    }, []);

    const handleMove = (move) => {
        sendMoveCommand(move);
    };

    const handleDeployment = (deploymentData) => {
        setDeployment(deploymentData);
        setIsDeploymentComplete(true); // Mark deployment as complete
    };

    return (
        <div>
            <h1>WebSocket App</h1>
            <button onClick={() => handleMove({ x: 1, y: 2 })}>Send Move</button>
            <div>
                <h2>Messages:</h2>
                <ul>
                    {messages.map((msg, index) => (
                        <li key={index}>{msg}</li>
                    ))}
                </ul>
            </div>
            {isDeploymentComplete ? (
                <>
                    <h2>Board:</h2>
                    <Board grid={grid} />
                </>
            ) : (
                <>
                    <h2>Character Deployment:</h2>
                    <DeploymentForm onDeploy={handleDeployment} />
                </>
            )}
        </div>
    );
}

export default App;
