import React, { useEffect, useState } from 'react';
import Board from './components/Board';
import Controls from './components/Controls';
import { connectWebSocket, sendMoveCommand } from './websocket';

const App = () => {
    const [gameState, setGameState] = useState({
        grid: Array(5).fill(Array(5).fill(null)),
        currentPlayer: '',
        gameOver: false,
        winner: ''
    });

    useEffect(() => {
        // Establish WebSocket connection
        const ws = connectWebSocket((message) => {
            const newGameState = JSON.parse(message.body);
            setGameState(newGameState);
        });

        return () => ws.disconnect();
    }, []);

    const handleMove = (character, direction) => {
        if (!gameState.gameOver) {
            sendMoveCommand(character, direction);
        }
    };

    return (
        <div>
            <h1>Chess-like Game</h1>
            <Board grid={gameState.grid} />
            <Controls onMove={handleMove} currentPlayer={gameState.currentPlayer} />
            {gameState.gameOver && <h2>Game Over! Winner: {gameState.winner}</h2>}
        </div>
    );
};

export default App;
