import React from 'react';
import Cell from './Cell';

const Board = ({ grid }) => {
    const defaultGrid = Array(5).fill(null).map(() => Array(5).fill(null));

    const displayGrid = Array.isArray(grid) && Array.isArray(grid[0])
        ? grid
        : defaultGrid;

    return (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(5, 50px)' }}>
            {displayGrid.map((row, rowIndex) =>
                row.map((cell, colIndex) => (
                    <Cell key={`${rowIndex}-${colIndex}`} value={cell} />
                ))
            )}
        </div>
    );
};

export default Board;
