import { Client } from '@stomp/stompjs';

let client;

export const connectWebSocket = (onMessage) => {
    client = new Client({
        brokerURL: 'ws://localhost:8080/game',
        onConnect: () => {
            client.subscribe('/topic/gameState', onMessage);
        },
    });

    client.activate();
    return client;
};

export const sendMoveCommand = (character, direction) => {
    if (client && client.connected) {
        client.publish({
            destination: '/app/move',
            body: JSON.stringify({ character, direction }),
        });
    }
};
