import React, { useState } from 'react';
import CombatLogic from './CombatLogic';
import Controls from './Controls';
import CurrentPlayer from './CurrentPlayer';
import DeploymentForm from './DeploymentForm';
import GameBoard from './GameBoard';
import MoveHistory from './MoveHistory';

function App() {
    const [gameState, setGameState] = useState({
        currentPlayer: 'A',
        board: Array(5).fill(null).map(() => Array(5).fill(null)),
        moveHistory: [],
        gameOver: false,
        winner: null,
    });

    const handleDeployment = (deployment) => {
        // Deploy players' characters based on the initial setup from DeploymentForm
        setGameState((prevState) => ({
            ...prevState,
            board: deployment,
        }));
    };

    const handleMove = (move) => {
        const { updatedBoard, updatedMoveHistory, gameOver, winner } = CombatLogic(gameState, move);
        setGameState({
            ...gameState,
            board: updatedBoard,
            moveHistory: updatedMoveHistory,
            currentPlayer: gameState.currentPlayer === 'A' ? 'B' : 'A',
            gameOver,
            winner,
        });
    };

    return (
        <div>
            <h1>5x5 Strategy Game</h1>
            {!gameState.gameOver ? (
                <>
                    <DeploymentForm onDeploy={handleDeployment} />
                    <GameBoard board={gameState.board} />
                    <CurrentPlayer player={gameState.currentPlayer} />
                    <MoveHistory history={gameState.moveHistory} />
                    <Controls onMove={handleMove} />
                </>
            ) : (
                <h2>Game Over! Winner: {gameState.winner}</h2>
            )}
        </div>
    );
}

export default App;
